CREATE TABLE IF NOT EXISTS "__EFMigrationsHistory" (
    "MigrationId" character varying(150) NOT NULL,
    "ProductVersion" character varying(32) NOT NULL,
    CONSTRAINT "PK___EFMigrationsHistory" PRIMARY KEY ("MigrationId")
);

START TRANSACTION;

CREATE TABLE public."Users" (
    "UserID" integer GENERATED BY DEFAULT AS IDENTITY,
    "FirstName" character varying(50) NOT NULL,
    "LastName" character varying(50) NOT NULL,
    "Email" text NOT NULL,
    "PasswordHash" text NOT NULL,
    CONSTRAINT "PK_Users" PRIMARY KEY ("UserID")
);

CREATE TABLE public."Addresses" (
    "AddressID" integer GENERATED BY DEFAULT AS IDENTITY,
    "Street" character varying(100) NOT NULL,
    "City" character varying(50) NOT NULL,
    "PostalCode" character varying(20) NOT NULL,
    CONSTRAINT "PK_Addresses" PRIMARY KEY ("AddressID")
);

CREATE TABLE public."Providers" (
    "ProviderID" integer GENERATED BY DEFAULT AS IDENTITY,
    "ProviderName" character varying(100) NOT NULL,
    "ContactPerson" character varying(50) NOT NULL,
    "PhoneNumber" text NOT NULL,
    "Email" text NOT NULL,
    CONSTRAINT "PK_Providers" PRIMARY KEY ("ProviderID")
);

CREATE TABLE public."Services" (
    "ServiceID" integer GENERATED BY DEFAULT AS IDENTITY,
    "ServiceName" character varying(100) NOT NULL,
    "UnitOfMeasure" character varying(20) NOT NULL,
    CONSTRAINT "PK_Services" PRIMARY KEY ("ServiceID")
);

