CREATE TABLE public."Contracts" (
    "ContractID" integer GENERATED BY DEFAULT AS IDENTITY,
    "UserID" integer NOT NULL,
    "AddressID" integer NOT NULL,
    "ServiceID" integer NOT NULL,
    "ProviderID" integer NOT NULL,
    "ContractNumber" character varying(50) NOT NULL,
    "ContractStartDate" timestamp with time zone NOT NULL,
    "ContractEndDate" timestamp with time zone,
    CONSTRAINT "PK_Contracts" PRIMARY KEY ("ContractID"),
    CONSTRAINT "FK_Contracts_Users_UserID" FOREIGN KEY ("UserID") REFERENCES public."Users" ("UserID") ON DELETE CASCADE,
    CONSTRAINT "FK_Contracts_Addresses_AddressID" FOREIGN KEY ("AddressID") REFERENCES public."Addresses" ("AddressID") ON DELETE CASCADE,
    CONSTRAINT "FK_Contracts_Services_ServiceID" FOREIGN KEY ("ServiceID") REFERENCES public."Services" ("ServiceID") ON DELETE CASCADE,
    CONSTRAINT "FK_Contracts_Providers_ProviderID" FOREIGN KEY ("ProviderID") REFERENCES public."Providers" ("ProviderID") ON DELETE CASCADE
);

CREATE INDEX "IX_Contracts_UserID" ON public."Contracts" ("UserID");

CREATE INDEX "IX_Contracts_AddressID" ON public."Contracts" ("AddressID");

CREATE INDEX "IX_Contracts_ServiceID" ON public."Contracts" ("ServiceID");

CREATE INDEX "IX_Contracts_ProviderID" ON public."Contracts" ("ProviderID");

