VALUES ('20251129183039_Contracts', '8.0.11');

COMMIT;

START TRANSACTION;

CREATE TABLE public."Invoices" (
    "InvoiceID" integer GENERATED BY DEFAULT AS IDENTITY,
    "ContractID" integer NOT NULL,
    "InvoiceDate" timestamp with time zone NOT NULL,
    "DueDate" timestamp with time zone NOT NULL,
    "TotalAmount" numeric NOT NULL,
    "IsPaid" boolean NOT NULL,
    "PaymentDate" timestamp with time zone,
    CONSTRAINT "PK_Invoices" PRIMARY KEY ("InvoiceID"),
    CONSTRAINT "FK_Invoices_Contracts_ContractID" FOREIGN KEY ("ContractID") REFERENCES public."Contracts" ("ContractID") ON DELETE CASCADE
);

CREATE TABLE public."Payments" (
    "PaymentID" integer GENERATED BY DEFAULT AS IDENTITY,
    "InvoiceID" integer NOT NULL,
    "PaymentDate" timestamp with time zone NOT NULL,
    "PaymentAmount" numeric NOT NULL,
    "PaymentMethod" character varying(30) NOT NULL,
    CONSTRAINT "PK_Payments" PRIMARY KEY ("PaymentID"),
    CONSTRAINT "FK_Payments_Invoices_InvoiceID" FOREIGN KEY ("InvoiceID") REFERENCES public."Invoices" ("InvoiceID") ON DELETE CASCADE
);

CREATE TABLE public."Notifications" (
    "NotificationID" integer GENERATED BY DEFAULT AS IDENTITY,
    "UserID" integer NOT NULL,
    "NotificationDate" timestamp with time zone NOT NULL,
    "NotificationType" character varying(20) NOT NULL,
    "NotificationText" character varying(200) NOT NULL,
    "IsRead" boolean NOT NULL,
    CONSTRAINT "PK_Notifications" PRIMARY KEY ("NotificationID"),
    CONSTRAINT "FK_Notifications_Users_UserID" FOREIGN KEY ("UserID") REFERENCES public."Users" ("UserID") ON DELETE CASCADE
);

CREATE TABLE public."UserAddresses" (
    "UserAddressID" integer GENERATED BY DEFAULT AS IDENTITY,
    "UserID" integer NOT NULL,
    "AddressID" integer NOT NULL,
    "IsPrimary" boolean NOT NULL,
    CONSTRAINT "PK_UserAddresses" PRIMARY KEY ("UserAddressID"),
    CONSTRAINT "FK_UserAddresses_Users_UserID" FOREIGN KEY ("UserID") REFERENCES public."Users" ("UserID") ON DELETE CASCADE,
    CONSTRAINT "FK_UserAddresses_Addresses_AddressID" FOREIGN KEY ("AddressID") REFERENCES public."Addresses" ("AddressID") ON DELETE CASCADE
);

CREATE TABLE public."MeterReadings" (
    "ReadingID" integer GENERATED BY DEFAULT AS IDENTITY,
    "ContractID" integer NOT NULL,
    "ReadingDate" timestamp with time zone NOT NULL,
    "ReadingValue" numeric NOT NULL,
    "SubmittedByUserID" integer,
    CONSTRAINT "PK_MeterReadings" PRIMARY KEY ("ReadingID"),
    CONSTRAINT "FK_MeterReadings_Contracts_ContractID" FOREIGN KEY ("ContractID") REFERENCES public."Contracts" ("ContractID") ON DELETE CASCADE,
    CONSTRAINT "FK_MeterReadings_Users_SubmittedByUserID" FOREIGN KEY ("SubmittedByUserID") REFERENCES public."Users" ("UserID")
);

