CREATE INDEX "IX_UserAddresses_AddressID" ON public."UserAddresses" ("AddressID");

CREATE INDEX "IX_MeterReadings_ContractID" ON public."MeterReadings" ("ContractID");

CREATE INDEX "IX_MeterReadings_SubmittedByUserID" ON public."MeterReadings" ("SubmittedByUserID");

